vec3 palette( float t ) {
    vec3 a = vec3(0.588, 0.048, 1.208);
    vec3 b = vec3(0.498, -1.192, -0.082);
    vec3 c = vec3(0.970, 1.085, -0.341);
    vec3 d = vec3(2.065, -0.245, 0.375);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 black = vec3(0.0);


    for (float i = 0.0; i < 2.0; i++) {

        uv = fract(sin(uv*1.45))-0.5;

        float d = length(uv);
        vec3 col = palette(length(uv0) + i*2.);


        d = sin(d*8. + iTime)/9.0;
        d = abs(d);

        d = pow(0.01/d, 1.2);
        black += col * d;
       }

    fragColor = vec4(black, 1.0);
}
