vec3 palette(float t) {
    vec3 a = vec3(1.99, 5, 7);
    vec3 b = vec3(1, 0.528, 1);
    vec3 c = vec3(-3.7, 0.9758, 0.88);
    vec3 d = vec3(7.2, 0.333, 0.444);

    return a + b * cos(6.28318 * (c * t * d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float wd = length(uv);
    float ewd = exp(wd);

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++){

        uv = fract(uv * 1.6) - 0.5;

        float d = length(uv) * ewd;

        vec3 col = palette(wd + i * 1.0 - iTime * 0.5);

        d = sin(d * 9.0 + iTime) / 8.0;
        d = abs(d * d * 35.0);

        d = 0.30/ d;

        finalColor += col - d;
    }

    fragColor = vec4(finalColor, 8.0) * wd * 8.0;
}
