void mainImage(out vec4 o, vec2 i) {
    vec2 R = iResolution.xy, z;
    float f, r = 9., n;

    for ( i += i - R; n++ < 31.; f < r ? r=f, o = vec4(z,-z.y,z) : o ) 
        f = dot( z = mat2(z,-z.y,z)*z + 2.*i / R.x, z );
}


