void mainImage(out vec4 o, vec2 i) {
    o.xyz = iResolution;
    mat2 z, t;
    float r = 9.;

    for (i += i - o.xy; o.z++ < 31.;) {
        // represent complex operations in matrix form
        z = z * z + 2. * mat2(i, -i.y, i) / o.x;

        // measure shortest approach distance
        float f = determinant(z);
        r = min(r, f);
        t = t * float(r != f) + float(r == f) * z;
    }

    // we can convert from mat2 to vec4 
    // since they both have 4 floats
    o = vec4(t);
}
