void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Define some basic colors
    vec3 lightGray = vec3(0.85, 0.85, 0.85);
    vec3 darkGray = vec3(0.2, 0.2, 0.2);
    vec3 blue = vec3(0.0, 0.3, 1.0);

    // Define the size of the image
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= aspect;

    // Draw the face
    float face = length(uv) * 2.0;
    float mouth = max(0.0, 0.5 - abs(uv.y) - abs(uv.x));
    float eye1 = max(0.0, 0.2 - length(vec2(uv.x + 0.2, uv.y - 0.1)));
    float eye2 = max(0.0, 0.2 - length(vec2(uv.x - 0.2, uv.y - 0.1)));
    float eyebrows = max(0.0, 0.2 - abs(uv.y - 0.2) - abs(uv.x * 0.3));
    vec3 faceColor = mix(darkGray, lightGray, face);
    vec3 mouthColor = mix(darkGray, blue, mouth);
    vec3 eyeColor = mix(darkGray, blue, max(eye1, eye2));
    vec3 eyebrowColor = mix(darkGray, lightGray, eyebrows);

    // Combine the colors to create the final image
    vec3 finalColor = mix(faceColor, mouthColor, mouth) * mix(eyeColor, eyebrowColor, eyebrows);
    fragColor = vec4(finalColor, 1.0);
}
