#define PI acos(-1.)

mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for (i=0.;i++<64.&&d>1e-4;){
    p=normalize(vec3((FC.xy-.5*iResolution.xy)/iResolution.y,1))*e;
    p.z-=24.;    
    p*=rotate3D(iTime*.2,vec3(-1));

    for (j=0.;j++<12.;)
        p=abs(abs(p*rotate3D(PI, vec3(-1)))-.3)-.1,
        d=length(p-.1)-.06,
        d-=dot(p,p)/1e4;

    e+=d;
}o+=vec4(-log(p),1)*3./i;
}
