/*
// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

uint uhash(ivec3 v)
{
    return triple32(uint(v.x) + triple32(uint(v.y) + triple32(uint(v.z))));
}
*/
uint uhash(ivec3 f) // faster prng suggested by Fabrice
{
    uvec3  x = uvec3(f);
    uint   k = 1103515245U; 
           x = ((x>>8U)^x.yzx)*k;
           x = ((x>>8U)^x.yzx)*k;  
    return uint(((x>>8U)^x.yzx)*k);
}

vec3 barycentric(vec2 p)
{
    float s = sqrt(3.0);
    return vec3((vec2(-1,+1) * p.x - p.y/s + 1.0)/2.0, p.y / s);
}

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec3 div_floor(ivec3 a, int b) // vector version thanks to Fabrice
{
    ivec3  S = (sign(abs(a*b))-sign(a*b))/2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1)+(1-S)*(a / b); // emulates ()?:
}

ivec3 mod_positive(ivec3 a, int b)
{
    return a - div_floor(a, b) * b;
}

ivec3 hexagonal(ivec3 i) // group triangular coordinates to hexagonal coordinates (jt)
{
    return div_floor(i.zxy-i.yzx+ivec3(0,-1,+1),3); // NOTE: permutate 0,-1,+1 to align hexagons differently
}

#define scale (20.0*sqrt(3.0))
//#define e (3./R.y*scale) /* smooth but blurry */
#define e (2./R.y*scale) /* less blurry */

void mainImage( out vec4 o, in vec2 I )
{
    float s = 0.05;
    float r = s * 1.25;
    vec2 R = iResolution.xy;
    //I = 2.0 * I/R - 1.0;
    I = (2.0 * I - R) / R.y; // mind aspect ratio

    vec3 t = barycentric(I*scale);

    ivec3 m = ivec3(floor(t));
    bool flip = m.x + m.y + m.z == 0;
    //bool tri = any(lessThan(t, vec3(0))); // fundamental triangle
    t = fract(t);
    //t -= vec3(m);
    if(!flip) t = 1.0 - t;

    uint choice = uhash(hexagonal(m) + int(floor(iTime))) % 3u;
    t = choice == 2u ? t.zxy : choice == 1u ? t.yzx : t.xyz;

    //float c = smoothstep(r-e,r+e,abs(abs(2.0*t.z-1.)-0.0/3.0)); // variant
    float c = smoothstep(r-e,r+e,abs(abs(2.0*t.z-1.)-1.0/3.0));
    //float c = smoothstep(r-e,r+e,abs(abs(2.0*t.z-1.)-3.0/3.0)); // variant

    o = vec4(1.0 - c);
    if(iMouse.z > 0.0)
    {
        o = max(o, vec4(0.2*(choice == 2u ? vec3(0,0,1) : choice == 1u ? vec3(0,1,0) : vec3(1,0,0)), 1.0));
        o += 0.5 - 0.5 * min(smoothstep(0.0,0.0+e,t.x), min(smoothstep(0.0,0.0+e,t.y), smoothstep(0.0,0.0+e,t.z)));
    }
    //if(tri) o.xyz = vec3(0,0,1); // fundamental triangle
    //if(flip) o.xyz = vec3(1,0,0);
    o = sqrt(o); // gamma
}
