float col(vec2 a, vec2 b, vec2 uv)
{
    float cos1 = dot(a, uv);
    float cos2 = dot(b, uv);
    float cos3 = dot(a, b);

    return float ((cos1) >= (cos3)) * float ((cos2) >= (cos3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 a = vec2(0.5, 0.75);
    vec2 b = vec2(0.25, 0.25);
    vec2 c = vec2(0.75, 0.25);

    vec2 ab = a - b;

    vec2 ac = a - c;

    vec2 bc = b - c;

    float q1 = col(normalize(ab), normalize(ac), -normalize(uv - a));

    float q2 = col(normalize(ab), normalize(bc), -normalize(uv - b));


    float w1 = col((ab), (ac), (uv - a));

    float w2 = col((ab), (bc), (uv - b));

    float w3 = col((ac), -(bc), (uv - c));

    vec3 col = (q1 - q2) * (0.7 + 0.5*cos(iTime+uv.xyx));


    fragColor = vec4(col, 1.0);

}
