#define N 3.
#define N2 1.



#define BG vec3(49, 52, 66)/255.
//vec3(69, 88, 176)/255.
#define C vec3(1.)
//vec3(125, 147, 250)/255.

float dist(vec2 uv, float time){
    float id = floor(uv.x);
    uv.x = fract(uv.x);
    float period = 1.;
    float t = mod(time*.7-id*.1, period)-period*.5;
    float d = length(uv-vec2(.5, .3+(period*period*.25-t*t)*10.))-.3;
    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    //vec2 uv0 = uv;

    uv *= .75;

    uv = vec2(N*atan(uv.y, uv.x)/(2.*3.14), (abs(length(uv)-.25))*3.);
    uv *= N2*10.;


    /*uv0 *= 3./2.;

    uv0 = vec2(N*atan(uv0.y, uv0.x)/(2.*3.14), (abs(length(uv0)-.25))*3.);
    uv0 *= 10.;*/



    vec3 col = C;
    float d = dist(uv, iTime);
    //float d2 = dist(uv0*2., -iTime);
    //float d2 = dist(uv, iTime+.02);
    //float d3 = dist(uv, iTime-.02);
    //float d3 = dist(uv-vec2(0., .2));
    /*vec3 col1 = mix(col, vec3(1., 0.8, 0.8), smoothstep(0., 0.07, d2));
    vec3 col2 = mix(col, vec3(0.8, 0.8, 1.), smoothstep(0., 0.07, d3));
    vec3 col3 = mix(col, vec3(0.8, 1., 0.8), smoothstep(0., 0.07, min(d2, d3)));
    col = col1+col2+col3;*/
    col = mix(col, BG, smoothstep(0., 0.05, d));

    //col += mix(col, BG, smoothstep(0., 0.07, d2));
    // Output to screen
    fragColor = vec4(col,1.0);
}
