#define rot(r) mat2(cos(r + vec4(0, 33, 11, 0)))
#define t iTime * 1.

float ring(vec3 p){
    float d = length(vec2(length(p) - .8, p.z)) - .1;
    return .8 * d;
}

void mainImage(out vec4 o, vec2 u) {    
    vec3 r = iResolution, p; o *= 0.;
         u = (u - r.xy / 2.) / r.y;  

    float c, d, i, j, g, e = 1.; 

    while(i++ < 1e2) {
        p = vec3(u.x * g, -u.y * g, g - 5. - i / 5e2);
        p.y += 1.6;

        j = 0.; 
        while(j++ < 9.)
            d = ring(p) * pow(.6, j) + cos(g) * .01,
            d < e 
                ? e = d, 
                  c = j 
                : c,
            p.xz *= rot(cos(t * .6) * 4.),
            p.xy = vec2(abs(p.x) - .2 , p.y - 1.1)
                    * rot(sin(t) * .52 + .785), 

            p *= 1.5;

        g += e;
        o += 2.e-5 / e;
    }
    o.xy *= c / 2.;
}
