const float PI = 3.14;
const float freq = PI * 0.4;

/**
 * @author rah, inspired by jonobr1 / http://jonobr1.com/
 */

/* 
 * Output circle colors at given center pos and radius
 */
vec4 circle(vec2 uv, vec2 center, float radius, vec3 color)
{
    // Get distance of point from center, get difference from given radius
	float d = length(center - uv) - radius;
	float t = clamp(d, 0.0, 1.0);

    // If point is smaller than radius, set color alpha to 1, otherwise 0
	return vec4(color, 1.0 - t);
}

/*
 * Your work here!
 */

float computeRadius(vec2 uv)
{
    float radius = 0.25 * iResolution.y;

    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);

    uvCenter = rotatePoint2d(uvCenter, vec2(0.0, 0.0), iTime);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y);  

    // No abs b/c we want negative value.
    float time = cos(iTime * 2.0);
    // Cos(iTime * freq) will increase frequency of oscillation
    // Thickness of flower
    float thickness = 20.0;
    // Width of flower
    float width = 80.0;
    // Triangle wave oscillates an angle a from centre at freq of PI/4 at amplitude 1.
    float result = radius + thickness + width * time * triangleWave(a, PI/4.f, 1.0);

    return result;
}

vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);
    return a + b*cos(6.28318 * (c*t + d));
}

vec3 palette2(float t) {
    vec3 a = vec3(-0.282, -0.002, 3.058);
    vec3 b = vec3(3.138, 3.138, 3.138);
    vec3 c = vec3(-0.442, 0.948, 1.058);
    vec3 d = vec3(0.358, 1.028, 2.588);
    return a + b*cos(6.28318 * (c*t + d));
}

vec3 palette3(float t) {
    vec3 a = vec3(1.338, 3.118, 0.398);
    vec3 b = vec3(2.605, 2.605, 2.605);
    vec3 c = vec3(-0.322, 0.568, 0.773);
    vec3 d = vec3(-0.532, 0.167, -0.333);
    return a + b*cos(6.28318 * (c*t + d));
}

vec4 funky1(vec2 uv, vec2 center, float radius, vec3 colour) {
    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);

    uvCenter = rotatePoint2d(uvCenter, vec2(0.0, 0.0), iTime);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y); 
    float time = cos(iTime * 0.5);
    // Thickness of flower
    float thickness = 20.0;
    // Width of flower
    float width = 80.0;
    // Triangle wave oscillates an angle a from centre at freq of PI/4 at amplitude 1.
    radius += thickness + width + radius * time * sawtoothWave(a, PI/4.f, 1.0);

    vec4 circleTime = circle(uv, center, radius, palette(time));

    return circleTime;
}

vec4 funky2(vec2 uv, vec2 center, float radius, vec3 colour) {
    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);

    uvCenter = rotatePoint2d(uvCenter, vec2(0.0, 0.0), iTime);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y); 
    float time = cos(iTime * 0.1);
    // Thickness of flower
    float thickness = 5.0;
    // Width of flower
    float width = 50.0;
    // Triangle wave oscillates an angle a from centre at freq of PI/4 at amplitude 1.
    radius += thickness + width + radius * time * triangleWave(-a, PI/4.f, 0.5) * abs(fract(a * 0.8)*-3.0-0.5);
    radius *= 0.7;

    vec4 circleTime = circle(uv, center, radius, palette2(time * 2.0));

    return circleTime;
}

vec4 funky3(vec2 uv, vec2 center, float radius, vec3 colour) {
    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);

    uvCenter = rotatePoint2d(uvCenter, vec2(0.0, 0.0), iTime);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y); 
    float time = cos(iTime * 0.1);
    // Thickness of flower
    float thickness = 10.0;
    // Width of flower
    float width = 60.0;
    // Triangle wave oscillates an angle a from centre at freq of PI/4 at amplitude 1.
    radius += thickness + width + radius * time * triangleWave(-a, PI/4.f, 0.5);
    radius *= 0.8;

    vec4 circleTime = circle(uv, center, radius, palette3(time * 2.0));

    return circleTime;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = computeRadius(uv);

    // Background layer
	vec4 layer1 = vec4(rgb(255.0, 255.0, 210.0), 1.0);

	// Circle
	vec3 red = rgb(225.0, 50.0, 70.0);
	vec4 layer2 = funky1(uv, center, radius, red);

    vec4 layer3 = funky2(uv, center, radius, red);

    vec4 layer4 = funky3(uv, center, radius, red);

	// Blend the two
	fragColor = mix(layer1, layer2, layer2.a);
    fragColor = mix(fragColor, layer4, layer4.a);
    fragColor = mix(fragColor, layer3, layer3.a);

}

/*
 * Toolbox functions! ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */ 
float bias(float t, float b) {
    return (t / ((((1.0/b) - 2.0)*(1.0 - t))+1.0));
}

float gain (float g, float t) {
    if (t < 0.5f) {
        return bias(1.0 - g, 2.0 * t) / 2.f;
    } else {
        return 1.0 - bias(1.0 - g, 2.0 - 2.0 * t) / 2.0;
    }
}

float sawtoothWave(float x, float freq, float amplitude) {
    return (x * freq - floor(x * freq)) * amplitude;
}

float triangleWave(float x, float freq, float amplitude) {
    return 2.0 * abs(mod((x * freq), amplitude) - (0.5 * amplitude));
}


/*
 * Basic Utilities ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */


/* 
 * Rotate an input point about some center by angle
 */
vec2 rotatePoint2d(vec2 uv, vec2 center, float angle)
{
    vec2 rotatedPoint = vec2(uv.x - center.x, uv.y - center.y);
    float newX = cos(angle) * rotatedPoint.x - sin(angle) * rotatedPoint.y;
    rotatedPoint.y = sin(angle) * rotatedPoint.x + cos(angle) * rotatedPoint.y;
    rotatedPoint.x = newX;
    return rotatedPoint;
}

/*
 * Make a vec3 color from rgb values [0, 255]
 */
vec3 rgb(float r, float g, float b)
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}
