void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float timer = 0.0;

    vec4 red = vec4(1,0,0,1);
    vec4 green = vec4(0,1,0,1);
    vec4 blue = vec4(0,0,1,1);

    timer = (timer+iTime)*0.5;

    timer = clamp(timer,0.0,1.0);

    // Output to screen
    fragColor = mix(red,green,timer);
}
