#define P 4.
#define Q 5.

#define iters 30

#define TWOPI 6.2831853
#define PI 3.1415926
#define S23 0.2679491
#define SS2 0.7071067
#define R vec2(1,0)
#define I vec2(0,1)

vec2 cmul(vec2 z, vec2 c) {
    return vec2(z.x * c.x - z.y * c.y, z.x * c.y + z.y * c.x);
}

vec2 cdiv(vec2 z, vec2 c) {
    float r = dot(c, c);
    return vec2(z.x * c.x + z.y * c.y, z.y * c.x - z.x * c.y) / r;
}

vec2 conj(vec2 z) {
    return vec2(z.x, -z.y);
}

vec2 hypershift(vec2 z, vec2 s) {
    return cdiv(z + s, cmul(z,conj(s))+vec2(1.0,0.0));
}

vec2 cpow(vec2 z, vec2 p) {
    float a = atan(z.y, z.x);
    float lnr = 0.5 * log(dot(z,z));
    float m = exp(p.x * lnr - p.y * a);
    float angle = p.x * a + p.y * lnr;
    return vec2(cos(angle), sin(angle)) * m;
}

vec2 cabs(vec2 z, vec2 p) {
    return cpow(cpow(z,p), cdiv(R,p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 2.0 * (fragCoord.xy - 0.5*iResolution.xy) / -iResolution.y;
    float r = PI/3.;
    vec3 c = vec3(0.3,0.2,0.);
    float t = iTime * 0.25;
    float tt = tan(iTime * 0.25);

    u = cdiv(u+R*tt,cmul(u,-R*tt)+R);
    u = dot(u,u) > 1. ? cdiv(-R,u) : u;
    u = cmul(1728.*cpow(u,5.*R),cdiv(cpow(cpow(u,10.*R)-11.*cpow(u,5.*R)-R,5.*R), cpow(cpow(u,20.*R)+228.*cpow(u,15.*R)+494.*cpow(u,10.*R)-228.*cpow(u,5.*R)+R, 3.*R)));

    u = cpow(u,R*0.5);
    u = cpow(cmul(cdiv(u+I,cmul(u,I)+R),vec2(SS2)),R*2.);
    u = cmul(cdiv(u-R,u+R),vec2(vec2(cos(t),sin(t))));
    u = dot(u,u)>1.?cdiv(-R,u):u;

    if(hypershift(cmul(u,vec2(cos(r),sin(r))),R*S23).x < 0.) {
        u = -cabs(u,3.*R);
        u = hypershift(u,R*S23);
        u = cdiv(u+R,-u+R);
        for(int i = 0; i < iters; i++) {
            vec2 uc = u*2.+I;
            vec2 lc = u*2.-I;
            if(dot(uc,uc) < 1.) {
                    u = uc;
                }
                if(dot(lc,lc) < 1.) {
                    u = lc;
                }
            }
        u = cdiv(u-R,u+R);
        u = hypershift(u,R*S23);
    } else if (hypershift(cmul(u,vec2(cos(-r),sin(-r))),R*S23).x < 0.) {
        u = -cabs(u,3.*R);
        u = hypershift(u,R*S23);
        u = cdiv(u+R,-u+R);
        for(int i = 0; i < iters; i++) {
            vec2 uc = u*2.+I;
            vec2 lc = u*2.-I;
            if(dot(uc,uc) < 1.) {
                    u = uc;
                }
                if(dot(lc,lc) < 1.) {
                    u = lc;
                }
            }
        u = cdiv(u-R,u+R);
        u = hypershift(u,R*S23);
        u = cmul(u, vec2(-cos(r),sin(r)));
    } else if (hypershift(-u,R*S23).x < 0.) {
        u = -cabs(u,3.*R);
        u = hypershift(u,R*S23);
        u = cdiv(u+R,-u+R);
        for(int i = 0; i < iters; i++) {
            vec2 uc = u*2.+I;
            vec2 lc = u*2.-I;
            if(dot(uc,uc) < 1.) {
                    u = uc;
                }
                if(dot(lc,lc) < 1.) {
                    u = lc;
                }
            }
        u = cdiv(u-R,u+R);
        u = hypershift(u,R*S23);
        u = cmul(u, vec2(-cos(r),-sin(r)));
    } else {
        u = u;
    }

    float a = atan(u.y,u.x);
    c += a > r ? vec3(0.7,0,0) : a > -r && a < r ? vec3(0.7,0.25,0) : vec3(0.7,0.5,0);

    u /= -S23;
    u = -cabs(u,3.*R);
    u = hypershift(u,R*S23);
    vec3 col = u.x < 0. && u.y > -1. && u.y < 1. || dot(u,u) < 1. ? c : vec3(0);
    fragColor = vec4(col,1.0);
}
