void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.x/t1/2.0;
    uv += vec2(iTime/6.0,iTime/2.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    col = fractal(uv);
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

//remix of https://www.shadertoy.com/view/mtSBzV

int collatz(int initial, int steps){
    //makes some interesting rhythms
    for(int i = 0; i < steps; i++){
        if(initial%2==0) initial /= 2;
        else initial = initial*3+1;
    }
    return initial;
}
float collatz(float initial, float steps){
    return float(collatz(int(initial),int(steps)));
}


float fibonacci(float nthNumber_) {
        int nthNumber = int(nthNumber_);
        int previouspreviousNumber, previousNumber = 0, currentNumber = 1;
        for (int i = 1; i < nthNumber ; i++) {

            previouspreviousNumber = previousNumber;

            previousNumber = currentNumber;

            currentNumber = previouspreviousNumber + previousNumber;

        }
        return float(currentNumber);
}

