vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract(a1*(a2.x+a2.y))-.5)
    ;
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    vec2 t2 = vec2(0.);
    bool b = true;
    for(int k = 0; k < 6; k++){
        uv =
            -abs(.5-uv-t2)/1.5
        ;

        //makes a black and white zellij pattern
        //uv = abs(fract(vec2(uv+vec2(.5,1.5))*1.5)-.5)/1.5;

        b =
            uv.y<uv.x
        ;
        if(b){
            uv = uv.yx;
        }
        t2 =
            triangle_wave(uv-.5).yx
        ;
        uv =
            t2-triangle_wave(uv.yx).yx
        ;
        vec2 uv1 =
            uv
        ;
        col.x =
            max(.5,col.x)            
        ;

        col =
            abs(col-(1.+col.x*sign(uv1.x-uv1.y)))
            //abs(col-(float(b)))
            //abs(col-(.5+sign(uv.x-uv.y)/2.))
            //vec3(col.zx,max(col.y,max(uv.y-uv.x,0.)))
            //max((col-1.+col.x/1.5),(1.-col-col.yzx))
        ;
        //if(!b) col=col.yzx;

    }
    if (!b) col = abs(1.-col);
    return col;
}
