vec3 Grid(in vec2 uv)
{
   vec3 color = vec3(0.0);
   vec2 faction = 1.0-2.0*abs(fract(uv)-0.5);
   //vec2 faction = fract(uv);
   if(abs(uv.x) <= 2.0 * fwidth(uv.x))
   {
      color.g = 1.0;
   }
   else if (abs(uv.y) <= 2.0 * fwidth(uv.y))
   {
      color.r = 1.0;
   }
   else if (faction.x < 2.0 * fwidth(uv.x) || faction.y< 2.0 * fwidth(uv.y))
   {
      color=vec3(1.0);
   }
   return color;
}
float Segment(in vec2 p, in vec2 a, in vec2 b, in float w) 
{
    float f = 0.0;
    vec2 ba = b - a;
    vec2 pa = p - a;
    float proj = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float d = length(proj * ba - pa);
    if (d <= w) {
        f = 1.;
    }
    return f;
}

vec2 fixUV(in vec2 c)
{
   return(2.0 * c - iResolution.xy) / min(iResolution.x,iResolution.y)*3.0;
}
float func(in float x)
{
   return sin(x);
}
float funcPlot(in vec2 uv) 
{
    float f = 0.0;
    for(float x = 0.0; x <= iResolution.x; x += 1.0) 
    {
        float fx = fixUV(vec2(x, 0.0)).x;
        float nfx = fixUV(vec2(x + 1.0, 0.0)).x;
        f += Segment(uv, vec2(fx, func(fx)), vec2(nfx, func(nfx)), 2.0*fwidth(uv.x));
    }
    return clamp(f, 0.0, 1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fixUV(fragCoord);
   vec3 color = Grid(uv);
   //color = mix(color,vec3(1.0,1.0,0.0),Segment(uv,vec2(4.0,2.0),vec2(-2.0,-2.0),fwidth(uv.x)));
   color = mix(color,vec3(1.0,1.0,0.0),funcPlot(uv));
   fragColor = vec4(color,1.0);
}
