#define PI 3.1415926583;

float Dot (vec2 uv, float x, float y) {
    return smoothstep(.1, .0, length(uv-vec2(x,y))-.02);
    }

vec3 col = vec3(0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= .95;
    float d=length(uv);

    float t=iTime/1.2 ;
    float a=t/900.+2., b=t/500.+7., c=t/3700.+8.;

    for(float i = 1.; i < 5.; ++i)
    {
    float p=(0.-.1*i)*PI;
    col += vec3(0,1,1)*Dot(uv, (sin(t*a+p)*cos(t*b+p))/2., sin(t*c+p)/3.);
    p=(-.1-.1*i)*PI;
    col += vec3(1,1,0)*Dot(uv, (sin(t*a+p)*cos(t*b+p))/2., sin(t*c+p)/3.);
    p=(-.2-.1*i)*PI;
    col += vec3(1,1,0)*Dot(uv, (sin(t*a+p)*cos(t*b+p))/2., sin(t*c+p)/3.);
    p=(-.3-.1*i)*PI;
    col += vec3(1,0,0)*Dot(uv, (sin(t*a+p)*cos(t*b+p))/2., sin(t*c+p)/3.);
    }

    fragColor = vec4(col,1.);
}
