void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord/iResolution.yy);// - vec2(16./9.,1.)/4.;
    vec3 color = vec3(0.0);

    float var1 = .1 + cos(iTime+0.4)/15. ;
    float var2 = .6 + sin(iTime-0.1)/13.;
    float var3 = .7; - cos(iTime)/8.;
    float var4 = .9 - cos(iTime+0.4)/15.;
    float var6 = .9 - cos(iTime+0.8)/8.;
    float diff = 0.02;

    // black outlines

    float y1 = step(var1, st.x) + 1.-step(var1-diff, st.x);
    float y2 = step(var3, st.x) + 1.-step(var3-diff, st.x);
    float y3 = step(var6, st.x) + 1.-step(var6-diff, st.x);
    float ylines = y1 * y2 * y3;

    float xlines = (step(var2, st.y) + 1.-step(var2-diff, st.y)) * (step(var4, st.y) + 1.-step(var4-diff, st.y));

    float left = step(var1, st.x);
    float top = step(var4-diff, 1.-st.y);
    float bottom = step(var1, st.y);
    float short2 = 1.-left*top*bottom;

    // color rectangles
    float blue1 =  ((step(var6-diff, st.x)) + 1.-step(var3, st.x) * step(var4, 1.-st.y)); // right left top
    float blue2 =  1.-step(var6, st.x) * step(var4, 1.-st.y);
    float blue = blue1 * blue2;

    float red1 = 1. - (1.-step(var1-diff, st.x)) * step(var2, st.y) * (1.-step(var4-diff, st.y)); // right bottom top
    float red2 = 1. - step(var1, st.x) * (1.-step(var3-diff, st.x)) * (1.-step(var2-diff, st.y)) * step(var1+diff, st.y); // left right top bottom
    float red = red1 * red2;

    float yellow1 = 1. - step(var3, st.x) * step(var4, st.y) * (1.-step(var6-diff, st.x)); // left bottom top
    float yellow2 = 1. - step(var3, st.x) * (1.-step(var6-diff, st.x)) * (1.-step(var4-diff, st.y)) * step(var2, st.y); // left right top bottom
    float yellow =yellow1 * yellow2;


    float pct = ylines * xlines * short2;
    color = vec3(pct);
    // add blue
    color = mix(vec3(0.,0.,1.), color, blue);

    // add red
    color = mix(vec3(1.,0.,0.), color, red);

    // add yellow
    color = mix(vec3(1.000,0.882,0.066), color, yellow);

    fragColor = vec4(color,1.0);
}
