// Simple raymarching example of a spinning blue cube on Shadertoy

vec3 rotate(vec3 p, vec3 angles) {
    vec3 c = cos(angles);
    vec3 s = sin(angles);

    mat3 rx = mat3(
        1.0, 0.0, 0.0,
        0.0, c.x, -s.x,
        0.0, s.x, c.x
    );

    mat3 ry = mat3(
        c.y, 0.0, s.y,
        0.0, 1.0, 0.0,
        -s.y, 0.0, c.y
    );

    mat3 rz = mat3(
        c.z, -s.z, 0.0,
        s.z, c.z, 0.0,
        0.0, 0.0, 1.0
    );

    return rz * ry * rx * p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

    // Camera and ray setup
    vec3 camPos = vec3(0.0, 0.0, 5.0);
    vec3 rayDir = normalize(vec3(uv, -1.0));

    // Rotating the scene
    float angle = iTime;
    vec3 angles = vec3(angle, angle, angle);

    // Raymarching
    float t = 0.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = camPos + t * rayDir;
        vec3 rotatedP = rotate(p, angles);
        float d = length(max(abs(rotatedP) - vec3(1.0), 0.0)) - 0.1;
        if (d < 0.001) break;
        t += d;
    }

    // Coloring the cube
    vec3 color = vec3(0.0, 0.0, 1.0) * (1.0 - t * 0.1);
    fragColor = vec4(color, 1.0);
}
