#define PIXW (5./iResolution.y)
#define PI 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    uv*=5.0;
    uv = fract(uv);
    uv-=0.5;

    vec3 result = vec3(smoothstep(0.4 + 2.0*PIXW, 0.4, length(uv)));

    // uv=vec2(length(uv),atan(-uv.y,-uv.x));
    // vec4 O=mix(vec4(0),O,uv.y/6.28+.5);

    uv = vec2(length(uv), atan(-uv.y, -uv.x));

    // 0-1
    float mixCoef = 0.5 + uv.y / (2.0 * PI);

    mixCoef = clamp(0.0, 1.0, mixCoef/fract(iTime));

    result = mix(vec3(1.0, 0.0, 0.0), result, mixCoef);

    fragColor = vec4(result, 1.0);
}
