float calcDist(vec2 p1, vec2 p2){
    float a = (abs(p1.x-p2.x)) * (abs(p1.x-p2.x));
    float b = (abs(p1.y-p2.y)) * (abs(p1.y-p2.y) * 0.33);
    return a + b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0,0,0);
    col = vec3((0.00001*(1.5+cos(iTime/3.0)/2.0))/((uv.y-0.5)*(uv.y-0.5)*(uv.x-0.5)*(uv.x-0.5))
    ,(0.00001*(1.5+cos(iTime/2.0+0.5)/2.0))/((uv.y-0.5)*(uv.y-0.5)*(uv.x-0.5)*(uv.x-0.5))
    ,(0.00001*(1.5+cos(iTime/4.0+1.0)/2.0))/((uv.y-0.5)*(uv.y-0.5)*(uv.x-0.5)*(uv.x-0.5)));
    col *= vec3((sin(iTime/2.0)+1.5)/50.0,(sin(iTime+0.33)+1.5)/50.0,(sin(iTime/1.5+0.66)+1.5)/50.0);

    //col *= (abs(uv.x-0.5)*abs(uv.x-0.5)+abs(uv.y-0.5)*abs(uv.y-0.5));

    fragColor = vec4(col,1.0);
}
