vec3 smooth_checker(vec2 NDC)
{
    const float tau = 6.2831853071795862;

    float count = 0.5;
    NDC = mod(NDC * count, 2.0);

    float d = sin(tau/2.+NDC.x*tau) * cos(-tau/4.+NDC.y*tau),
          fw = .8*fwidth(d);
          //fw = 32./iResolution.y;
    return vec3(smoothstep(-fw, fw, d));
}
//smooth_checker provided in comments by envy24

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{

    float tiles = 8.0; // Number of checkerboard tiles
    float warpAmount = iTime/35.0; //the strength of the warp

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 coord = vec2(uv.x * tiles, uv.y * tiles);


    vec2 warp = vec2(noise(coord * warpAmount), noise((coord + vec2(5.0, 10.0)) * warpAmount));
    coord += warp;


    float x = floor(coord.x);
    float y = floor(coord.y);
    float checker = smooth_checker(coord).x;

    fragColor = vec4(checker, checker, checker, 1.0);

}
