/*
// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

uint uhash(ivec3 v)
{
    return triple32(uint(v.x) + triple32(uint(v.y) + triple32(uint(v.z))));
}
*/
uint uhash(ivec3 f) // faster prng suggested by Fabrice
{
    uvec3  x = uvec3(f);
    uint   k = 1103515245U;
           x = ((x>>8U)^x.yzx)*k;
           x = ((x>>8U)^x.yzx)*k;
    return uint(((x>>8U)^x.yzx)*k);
}

vec3 barycentric(vec2 p) // via 3x3 matrix (omitting last vector)
{
    // essential 2x3 matrix
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat2x3 T =
        mat2x3
        (
            normalize(vec3(-1,+1, 0)),
            normalize(vec3(-1,-1, 2))
            //normalize(vec3(-1,-1,-1))
        );
    return T*p/sqrt(2.0); // additionally rotate 90 degrees to align hexagonal coordinates with triangular coordinates
}

int div_floor(int a, int b)
{
    return (a > 0 && b < 0) ? ((a - 1) / b) - 1 : (a < 0 && b > 0) ? (a + 1) / b - 1 : a / b; // codegolf this line
    //return (a ^ b) < 0 && a != 0 ? (1 - abs(a)) / abs(b) - 1 : a / b; // fenix found this at https://blog.demofox.org/2022/07/21/rounding-modes-for-integer-division/
    //return a * b < 0 // golfed version by Fabrice
    //           ? (1 - abs(a)) / abs(b) - 1 
    //           : a / b; 
}

int mod_positive(int a, int b)
{
    return a - div_floor(a, b) * b;
}

uint kisrhombille(ivec3 tile)
{
    if(mod_positive(tile.x-tile.y, 3) == 0) return 0u;
    if(mod_positive(tile.y-tile.z, 3) == 0) return 1u;
    if(mod_positive(tile.z-tile.x, 3) == 0) return 2u;
    // rotated
    //if(mod_positive(tile.x-tile.y, 3) == 0) return 1u;
    //if(mod_positive(tile.y-tile.z, 3) == 0) return 2u;
    //if(mod_positive(tile.z-tile.x, 3) == 0) return 0u;
}

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec3 div_floor(ivec3 a, int b) // vector version thanks to Fabrice
{
    ivec3  S = (sign(abs(a*b))-sign(a*b))/2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1)+(1-S)*(a / b); // emulates ()?:
}

ivec3 mod_positive(ivec3 a, int b)
{
    return a - div_floor(a, b) * b;
}

ivec3 hexagonal(ivec3 i) // group triangular coordinates to hexagonal coordinates (jt)
{
    return div_floor(i.zxy-i.yzx,3);
}

uint orientation(ivec3 tile)
{
    if((int(iTime/2.0)&1)!=0) tile = hexagonal(tile); // alternate triangular-hexagonal structure
    //return kisrhombille(tile);
    return uhash(tile + int(iTime)) % 3u;
}

vec3 permute(uint direction, vec3 local)
{
    return direction == 0u ? local : direction == 1u ? local.yzx : local.zxy; // this configuration would also work with directions 0,+1,-1
}

ivec3 unpermute(uint direction, ivec3 local)
{
    return direction == 0u ? local : direction == 1u ? local.zxy : local.yzx; // this configuration would also work with directions 0,+1,-1
}

ivec2 permute(bool flag, ivec2 v)
{
    return flag ? v.yx : v;
}

vec3 color(uint direction)
{
    return direction == 0u ? vec3(1,0,0) : direction == 1u ? vec3(0,1,0) : vec3(0,0,1) ; // this configuration would also work with directions 0,+1,-1
}

#define scale (5.0*sqrt(3.0))
//#define e (3./R.y*scale) /* smooth but blurry */
#define e (1./R.y*scale) /* less blurry */

void mainImage( out vec4 o, in vec2 I )
{
    vec2 R = iResolution.xy;
    //I = 2.0 * I/R - 1.0;
    I = (2.0 * I - R) / R.y; // mind aspect ratio

    vec3 global = barycentric(I*scale);

    ivec3 tile = ivec3(floor(global));
    bool even = ((tile.x ^ tile.y ^ tile.z) & 1) == 0;
    //bool tri = any(lessThan(global, vec3(0))); // fundamental triangle
    vec3 local = fract(global);
    //vec3 local = global - vec3(tile);
    if(even) local = 1.0 - local;

    uint direction = orientation(tile);
    local = permute(direction, local);

    bool side = (local.y-local.x) <= 0.0;

    // group triangle halfs with matching adjacent direction
    if(orientation(tile + unpermute(direction, ivec3(permute(side, ivec2(even?+1:-1,0)),0))) == direction)
        o = vec4(color(direction),1);
    else
        //o = vec4(1.0 - (0.25 + 0.5 * vec3(side) * color(direction)),1);
        //o = vec4(vec3(0),1);
        o = vec4(0.02 * vec3(side),1);

    if(true) // apply a "sim city" resembling color mapping from https://www.shadertoy.com/view/cscyR8 three directions city grid color
    {
        // color-change matrix
        mat3 C =
            mat3
            (
                vec3(0.0, 0.0, 0.5),
                vec3(0.1, 0.5, 0.0),
                vec3(1.0, 0.4, 0.0)
            );
        o.xyz = C * o.xyz; // tweak colors
    }

    if(iMouse.z > 0.0)
    {
        o = mix(o, vec4(0.2*(direction == 2u ? vec3(0,0,1) : direction == 1u ? vec3(0,1,0) : vec3(1,0,0)), 1.0), 0.75);
        o *= 0.5 + 0.5 * min(smoothstep(0.0,0.0+e,local.x), min(smoothstep(0.0,0.0+e,local.y), smoothstep(0.0,0.0+e,local.z)));
    }
    //if(tri) o.xyz = vec3(0,0,1); // fundamental triangle
    //o.xyz = local;
    o = sqrt(o); // gamma
}
