float easeOutElastic(float x) {
float c4 = (2.0 * 3.14) / 3.0;

return x == 0.0
  ? 0.0
  : x == 1.0
  ? 1.0
  : pow(2.0, -10.0 * x) * sin((x * 10.0 - 0.75) * c4) + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float w = (0.5 - (uv.x)) * (iResolution.x / iResolution.y);
    float h = 0.5 - uv.y;
	float distanceFromCenter = sqrt(w * w + h * h);

	float sinArg = (distanceFromCenter * 2.0 - iTime * 2.0);
	float slope = easeOutElastic(cos(sinArg));
    float slope2 = sin(sinArg) ;
	vec4 color = texture(iChannel0, uv + normalize(vec2(w, h)) * slope * 0.05);
    vec4 tColor = texture(iChannel1, uv + normalize(vec2(w, h)) * slope2 * 0.05);


	fragColor = mix(color, tColor, step(slope, slope2));
}
