void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    float time = iTime;

    // Coordenadas desplazadas por las ondas
    vec2 offset = vec2(sin(uv.y * 10.0 + time * 5.0), cos(uv.x * 10.0 + time * 5.0));
    vec2 displacedUV = uv + offset * 0.05;

    // Gradiente de rojo a celeste
    vec3 color = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 1.0), displacedUV.y);

    fragColor = vec4(color, 1.0);
}
