// GregRostami Version
void mainImage( out vec4 O, vec2 U )
{   
    float e, I = fract(U = 8.*U/iResolution.x - 2.3).y;
    for( O -= O; (O.a+=.1) < 2.; O += e<.03 ? e*40. : 0. )
         e = I * length(U+O.a),
         U *= mat2( sin( (O.a+iTime)*.2 + vec4(0,33,11,0) ) );
    O = 1.2-O;
}





