vec3 palette( float t) {

    vec3 a = vec3(-0.352, -0.182, 0.298);
    vec3 b = vec3(3.076, -0.776, 0.253);
    vec3 c = vec3(3.035, 5.661, 3.838);
    vec3 d = vec3(1.657, 4.127, 2.557);

    return a + b*sin(6.28318*(c+t+d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = (uv - 0.5) * 2.0;
    uv.x *= iResolution.x / iResolution.y;
    vec2 uv0=uv;

    vec3 finalColor = vec3(0.0);
    for(float i = 0.;i<3.;i++){
        uv= fract(uv * 2.) -0.5;
        float d = length(uv) * exp(-length(uv0));
        vec3 col = palette(length(uv0)+ i*4. + iTime/1.5);
        d = tan(d*8.0 + iTime)/8.0;
        d = abs(d);

        d = pow(0.0009 / d, 1.);

        finalColor += col * d;
     }

    fragColor = vec4(finalColor ,1.0);
}
