#define BARREL



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Normalized pixel coordinates (from -0.5 to 0.5)
    uv -= vec2(0.5);


    #ifdef BARREL

    float k = 1.;// facteur de distortion
    float n = 1.;// facteur de normalisation

    //distortion
    float l = length(uv);//distance depuis le centre
    uv *= (1.0+k+l*l);

    //normalize
    uv /= (1.0+k*n*n);

    #else

    float k = 1.;// facteur de distortion
    float n = 1.3;// facteur de normalisation
     //distortion
    float l = length(uv);//distance depuis le centre
    uv /= (1.0+k+l*l);

    //normalize
    uv *= (1.0+k*n*n);

    #endif

    //Normalized pixel coordinates (from 0 to 1)
    uv += vec2(0.5);

    // Time varying pixel color
    vec4 col= vec4(0.);
    //supprime les répétition de texture
    if(uv.x >= 0. && uv.x <=1. &&uv.y >=0. && uv.y <=1.)
    {
        col = texture(iChannel0,uv);
    }

    // Output to screen
    fragColor = col;
    //fragColor = vec4(vec3(uv.x),1.);
}
