vec2 Offset = vec2(0.00010);
float Multiplier = 20.0;

#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 getDiffVec3(vec2 uv, vec2 offset)
{
    vec4 o1 = (texture(iChannel0, uv - offset) * 2.0 - vec4(1.0));
    vec4 o2 = (texture(iChannel0, uv + offset) * 2.0 - vec4(1.0));
    return cross(o1.xyz,o2.xyz) * Multiplier;
}

float getDiff(vec2 uv, vec2 offset)
{
    return 1.0 - length(getDiffVec3(uv, offset));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float col = getDiff(uv, Offset) 
    * getDiff(uv, rotate2d(PI/8.0) * Offset)
    * getDiff(uv, rotate2d(PI/4.0) * Offset)
    * getDiff(uv, rotate2d(- PI/4.0)* Offset )
    * getDiff(uv, vec2(0.0));

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
