#define myFract(V)  ( abs( fract(V) - .5 ) - .25 )

#define S(v) smoothstep( -.8,.8,  (v)*R.y )

void mainImage( out vec4 O, vec2 u )
{
   vec2  R = iResolution.xy ,
         U = ( u - R/2. ) / R.y, V;
   float t = iTime,
         d =  S( length(U) - .4);       // circle(uv)
   V = U * 20.;
   V.x += step(1., mod(V.y-t,2.))*.5;
   float d2 = S( myFract(V.x)/20. );      // vert(uv)
   V = U * 10.;
   V.x += step(1., mod(V.y*5.,2.))*.5 -t/4.;
   float d3 = S( myFract(V.x)/10. );      // horiz(uv)

   O = vec4( mix(d2,d3,d) );
}


