vec3 nathash( vec2 coord, float t){
    vec3 f;
    float fx = fract((coord.x + 134.679 * coord.y) * 1.618 * fract(fract(iTime*t + coord.x / 156.1618  *mix(1.1618,6.1618,coord.y)) / fract(-iTime*t + coord.x / 26.1618  *mix(1.1618,6.1618,t*coord.y))));
    float fy = fract((coord.y - 316.497 * coord.x) * .618 * fract(fract(-iTime*t - coord.y / 26.1618  *mix(1.1618,6.1618,coord.x)) / fract(iTime*t - coord.y / 16.1618  *mix(1.1618,6.1618,coord.x))));
    float fz = fract((length(coord) + 3976.431) * 2.618 * fract(fract(iTime*t - length(coord) / 61.1618  *mix(1.1618,6.1618,length(coord))) / fract(-iTime*t - length(coord) / 61.1618  *mix(1.1618,6.1618,length(coord)))));
    return f = vec3(fx, fy, fz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float size = 16.;
    vec2 coord;
    vec2 uv = floor(fragCoord/size)*size/iResolution.xy;
    vec3 f;
    float num = size;

    for (float i = 0.; i < num; i++){
        coord = floor(fragCoord/(num-i))*(num-i);
        f *= .5;
        //f += mix(vec3(0.),vec3(1.),nathash(coord + fract(i*6.18), .001618*(i+1.)))*.5;
        f += nathash(coord + fract(i*6.18), .001618*(i+1.))*.5;
    }
    //f /= size;


    // Time varying pixel color
    vec3 col = f;

    // Output to screen
    fragColor = vec4(col,1.0);
}
