#define PI 3.14159265359

vec3 colorA = vec3(0.149,0.141,0.912);
vec3 colorB = vec3(.0,0.833,0.224);
vec3 colorC = vec3(1.,0.,0.);
vec3 colorD = vec3(0., .5, .5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    //st.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);

    vec3 pct = vec3(st.x);
    float y = clamp(sin(iTime),-.05,.6);
    vec2 d = vec2(.7, y);
    float t = iTime;

	float circ = smoothstep(.3, .1, length(st - d));
    float bloom = distance(d, vec2(.5));

    circ += bloom;
    float ground = smoothstep(.18, sin(t), 1.5);

    pct.r = smoothstep(0.0,1.0, st.x);
    pct.g = sin(st.x*PI);
    pct.b = pow(st.x,0.5);

    color = mix(colorA, colorB, pct);

    // Plot transition lines for each channel

	color += circ * colorC;
    if(st.y < .25){
        color = ground * colorD;
    	fragColor = vec4(color, 1.0);
	} else {
    	fragColor = vec4(color,1.0);
    }
}
