vec3 interlacing(vec2 uv, vec2 fragCoord, float compression_power) {
    vec3 col = vec3(texture(iChannel0, uv));
    if (mod(fragCoord.y, compression_power) > 0.5) {
        col = vec3(0.0);
    }

    return col;
}

vec3 checkers_pattern(vec2 uv, vec2 fragCoord, float compression_power) {
    vec3 col = vec3(texture(iChannel0, uv));

    if ((mod(fragCoord.y, compression_power) > 0.5 || mod(fragCoord.x, compression_power) > 0.5) &&
        !(mod(fragCoord.y, compression_power) > compression_power - 1.5 && mod(fragCoord.x, compression_power) > compression_power - 1.5)) {
        col = vec3(0.0);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1.0, 0.0, 0.0);

    int compression_power = int(ceil(abs(sin(iTime / 4.0)) * 4.0)) + 1;

    if (uv.x < 0.329) col = vec3(texture(iChannel0, uv));
    if (uv.x > 0.331 && uv.x < 0.659) col = interlacing(uv, fragCoord, float(compression_power));
    if (uv.x > 0.661) col = checkers_pattern(uv, fragCoord, float(compression_power));

    fragColor = vec4(col,1.0);
}
