float dtl(vec2 xy, vec2 p1, vec2 p2) {
    vec2 ab = p2-p1;
    if(abs(ab.x)>1e-5) {
        float a = ab.y/ab.x;
        float c=p1.y-p1.x*a;
        return abs(a*xy.x-xy.y+c)/sqrt(a*a+1.0);
    } else {
        return abs(xy.x-p1.x);
    }
}

#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    const float n = 21.0;
    float cf = 0.0;
    for(float j=0.0;j<n;++j) {
        vec2 c0 = vec2(cos(j*PI*4.0/n+cos(iTime)/4.0)/1.5, sin(j*PI*4.0/n+cos(iTime)/4.0)/1.5);
        for(float i=j+1.0;i<n;++i) {
            vec2 c1 = vec2(cos(i*PI*4.0/n+sin(iTime/2.0)/5.0)/1.5, sin(i*PI*4.0/n+sin(iTime/2.0)/5.0)/1.5);
            float d= dtl(uv, c0,c1);
            d = pow(d,1.5)*10000.0;
            d = 2.0/(d+1.0);
            cf += d/4.0;
        }
    }
    vec3 col=vec3(clamp(cf*3.0,0.0,1.0),clamp(cf*3.0-1.0,0.0,1.0), clamp(cf*3.0-2.0,0.0,1.0));

    fragColor = vec4(col,1.0);
}
