void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    float angle = tan(iTime*0.01)+iTime*0.02;
    vec3 finalColor = vec3(0.0);
    for(int i = 0; i < 64; i++){;
    uv = abs(uv);
    uv -= 0.5;
    uv *=1.1;
    uv *= mat2(
        cos(angle),-sin(angle),
        sin(angle),cos(angle));
    }
    finalColor = vec3(sin(iTime),cos(iTime),1.0)*length(uv);
    fragColor = vec4(finalColor,1.0);
}
