void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(fract(uv.x * iTime) / length(iMouse.xy / iResolution.xy),sin(uv.y / iTime) * 8.,tan(mod((iMouse.x + iMouse.y) / iResolution.x, .5) * uv.y * iTime) / iTime));

    // Output to screen
    fragColor = vec4(col / distance(uv.xyx, iMouse.xyx / iResolution.xyx) / 5., 1.0);
}
