/* This method works for explicit ( y=f(x) ) and implicit ( d=f(x,y,...) ) functions. */
#define R                         ( iResolution )
#define AR                        ( R.x / R.y )                      // Aspect ratio.
#define UNIT                      ( 16.0 / R.y )                      // Affects AA and thickness
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )
#define SCENE_SCALE               ( 3.5 )

float vertical_line(vec2 NDC, float x0) { return SMAA(abs(NDC.x-x0)); }
float horizontal_line(vec2 NDC, float y0) { return SMAA(abs(NDC.y-y0)); }
float y_axis(vec2 NDC) { return SMAA(abs(NDC.x)); }
float x_axis(vec2 NDC) { return SMAA(abs(NDC.y)); }
float draw_explicit_func(vec2 NDC, float fx, float dfdx)
{
    float sdf = NDC.y - fx;
    return SMAA(abs(sdf) / sqrt(1.0 + dfdx * dfdx)); // absolute value / arclenght over unit length interval
}

vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin)
{
    /**/
    vec2 M = 
        iMouse.z > 0. ?
            iMouse.xy - R.xy * 0.5 : // map xy from [0; R.x][0; R.y] to [-R.x/2; R.x/2][-R.y/2; R.y/2]
            vec2(0);                 // supress mouse offset
    SC -= M;                         // apply mouse offset
    /**/
    SC = 2. * SC - R.xy;             // map xy from [0; R.x][0; R.y] to [-R.x; R.x][-R.y; R.y]
    SC /= R.y;                       // map xy from [-R.x; R.x][-R.y; R.y] to [-AR; AR][-1; 1]
    SC *= scale;                     // map xy from [-AR;AR][-1;1] to [-AR*scale; AR*scale][-scale; scale]

    return SC - origin;              // shift to new origin.
}

vec3 colorize_area_under_curve(
    vec2 NDC,
    float fx,
    float dfdx,
    vec2 range,
    vec3 color)
{
    float sign = 
        fx > 0. ? 1. : -1.;
    float sdf = (NDC.y - fx);

    vec3 c =
        (fx >= 0. && NDC.y < 0.) ||
        (fx < 0. && NDC.y >= 0.) ?
            color :
            mix( color, vec3(1,0,1), SMAA(sign*sdf / sqrt(1.0 + dfdx * dfdx)) );

    return 
        NDC.x >= min(range.x, range.y) &&
        NDC.x <= max(range.x, range.y) ?
            c :
            color;
}

float ceil_(float x)
{
    //return ceil(x);
    return 
        x >= 0. ?
            1. + float(int(x)) :
            float(int(x));
}
float deriv(float x)
{
    const float DX = 0.001;
    return ( ceil_(x+DX) - ceil_(x) ) / DX;
}
float def(float l, float r)
{
    float count = 10.,
        dx = (r - l) / (count - 1.),
        dxdx = dx * dx,
        I = 0.;

    for (float i = 1.; i < count; i += 1.)
    {
        float x = l + i * i * dxdx;
        I += ceil_(x);
    }

    return I * dx;
}
float indef(float x)
{
    return def(0., x);
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = map_to_centered_ndc(SC, SCENE_SCALE, vec2(0,-0.5));

    vec3 color = vec3(1.);

/* Define explicit function and derivative. */
#define func(x) ( ceil_(x) )
#define dfdx(x) ( deriv(x) )
#define I(x)    ( def(0., x) )
/**/     
    float fx = func(NDC.x), deriv = dfdx(NDC.x);
          fx = fx == -0. ? -0.001 : fx;

    float l = sinOSC(-AR * SCENE_SCALE, AR * SCENE_SCALE, iTime*0.3),
          r = sinOSC(-AR * SCENE_SCALE, AR * SCENE_SCALE, iTime);
    vec2 range = vec2(l, r);

    /* Ranges */
    color -= vertical_line(NDC, l)*.5;  color -= vertical_line(NDC, r)*.5;

    /* Area under curve. */
    color = colorize_area_under_curve(NDC, fx, deriv, range, color);

    /* Explicit function y=f(x) */
    color = mix(color, vec3(0,1,0), draw_explicit_func(NDC, fx, deriv));
    //color = mix(color, vec3(1,0,0), draw_explicit_func(NDC, I(NDC.x), fx));

    /* Coordinates axis */
    color -= x_axis(NDC); color -= y_axis(NDC);

    /* Cells */
#define CELL_SIZE 1
         NDC = mod(NDC, vec2(CELL_SIZE)); 
         color -= vertical_line(NDC, 0.)*.2;  color -= horizontal_line(NDC, 0.)*.2; // comment this line if you not use mod
         color -= vertical_line(NDC, -1.)*.2; color -= horizontal_line(NDC, -1.)*.2;         
         color -= vertical_line(NDC, 1.)*.2;  color -= horizontal_line(NDC, 1.)*.2; 
    /* */ 

    O = vec4(color,1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}
