renderpass.code:
#define iR (iResolution.xy)
#define P (1./iR.y)
#define S(x,t) smoothstep(x,x-4.*P,t)
vec2 rot(vec2 uv,float t)
{
    float c=cos(t),s=sin(t);
    return mat2x2(c,-s,s,c)*uv;
}
float body(vec2 uv,float d)
{
    vec2 huv=rot(uv,.17),euv=rot(uv,.22),buv=uv;
    euv.y+=0.04+d*.01;//off
    euv.x*=sign(euv.x);
    euv-=vec2(.16+d*.01,.1);//mid dis
    euv=abs(rot(euv,-.1)*vec2(2,1));//mirr rot
    huv-=vec2(.14,-d*.002);
    huv*=vec2(1.,20.*huv.y/(.9-huv.x));
    buv.x+=buv.y*buv.y*.1+d*.003;
    float h=S(.4,length(huv)),
         b=S(.26,length(buv.x))*step(uv.y,0.),
         e=S(.19-d*.01,length(dot(euv,vec2(1))));

    return max(max(h,e),b);
}
float dbar(vec2 p,vec2 a,vec2 b)
{
    b=b-a;p=p-a;
    return length(p-b*clamp(dot(p,b)/dot(b,b),0.,1.));
}
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (I-.5*iR)*P,suv=uv,e=vec2(0.,1.);
    vec4 z=e.xxxx,o=1.-z;
    //anim 
    uv+=vec2(.004*sin(max(.1,sin(iTime*2.))*10.),
    .12*sin(iTime*2.+2.)+.23)+.1*vec2(sin(iTime),1.2*cos(iTime));

    float otl=body(uv,0.);
    O= mix(vec4(.4,.6,1,1),z,otl);
    O= mix(O,o,body(uv*1.1,1.));  
    //face
    O=mix(O,z,min(otl,
    S(.2,length(uv-vec2(.5,.1)))));
    O=mix(O,z, S(.04,length(uv-vec2(0,.06))));
    //arm
    uv+=.12;
    uv=rot(uv,-1.+sin(iTime*10.)*.3);
    vec2 se=e.yx*.2,hp=se*.8;
    O=mix(O,z,S(.018,
    min( dbar(uv,e.xx,se),
         dbar(uv,hp,hp+vec2(.01,.03)))));
    //star
    suv=abs(suv-.3);
    O=mix(O,vec4(1.,.9,.2,1.)*2.,max(0.,sin(iTime*2.)+.5)*
    S(.1-dot(vec2(1.2),suv)/length(suv)*.04,
    length(dot(suv,vec2(1)))));
}
