mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float i,j,d=.3,e=.1; vec3 p;
fragColor=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=4.;
    p*=rotate3D(iTime,vec3(1));

    for(j=0.;j++<4.;)
        p=(abs(abs(p)-.037)-.1),
        d=max(max(p.x,p.y),p.z)-.1,
        d-=min(min(p.z,p.y),p.x)-.08,
        d+=dot(p,p)*1e-4;
    e+=d;
}fragColor+=vec4(hsv(exp(i),.8,5.),1)/i;
}
