void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 a = texture(iChannel0, uv).rgb;
    vec3 b = iTime + uv.xyx + vec3(0.0, 2.0, 4.0);

    vec3 col = sqrt((a.x * cos(b) + a.y * sin(b) - a.z) * 0.5 + 0.5);

    fragColor = vec4(col,1.0);
}

precision highp float;
precision highp int;

uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

vec2 noise(uvec2 p) {
    float U = uintBitsToFloat(
        hash(0x717bc129u ^ hash(uint(iFrame) ^ hash(p.x ^ hash(p.y)))) & 0x007fffffu | 0x3f800000u
    ) - 1.5;
    float V = uintBitsToFloat(
        hash(0xf00d5a7cu ^ hash(uint(iFrame) ^ hash(p.x ^ hash(p.y)))) & 0x007fffffu | 0x3f800000u
    ) - 1.5;
    return vec2(U,V);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 info = texture(iChannel0, vec2(0.5) / iResolution.xy);
    float frame = info.w;
    if (info.xy != iResolution.xy) frame = float(iFrame - 1);
    if (fragCoord == vec2(0.5)) {
        fragColor.xy = iResolution.xy;
        fragColor.z = 0.0;
        fragColor.w = frame;
        return;
    }
    vec2 uv = ((vec2(fragCoord.x, fragCoord.y - 0.5) + noise(uvec2(fragCoord))) * 2.0 - iResolution.xy) *
        max(1.35 / iResolution.x, 1.15 / iResolution.y);
    uv.x -= 0.75;

    vec2 z = uv;
    vec2 z2 = z * z;
    vec2 c = uv;

    int i;
    for (i = 0; i < 1024; i++) {
        if ((z2.x + z2.y) > 256.0) break;
        z.y = (z.x + z.x) * z.y + c.y;
        z.x = z2.x - z2.y + c.x;
        z2 = z * z;
    }
    float iter = sqrt(float(i) - log2(log2(z2.x + z2.y)) + 4.0);

    fragColor = vec4(mix(
        texture(iChannel0, fragCoord / iResolution.xy).rgb,
        i == 1024 ? vec3(0.0, 0.0, 1.0) : vec3(cos(iter), sin(iter), 0.0),
        1.0 / (float(iFrame) - frame)
    ),0.0);
}
