void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 把像素坐标映射到 [-1,1] 区间
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;

    float time = fract(iTime/ 3.0);

    // 计算百叶窗的位置和宽度
    float position = mix(-1.0, 1.0, time);
    float width = mix(0.1, 0.5, time);

    // 计算百叶窗的形状
    float d = abs(uv.x - position);
    float shape = smoothstep(0.0, width, d);


    // 计算输出图像
    vec3 color1 = texture(iChannel0, uv).rgb;
    vec3 color2 = texture(iChannel1, uv).rgb;
    vec3 color = mix(color1, color2, shape);

    // 输出结果
    fragColor = vec4(color, 1.0);
}
