void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv *= 100.;

    uv = round(uv);

    float n = mod(iTime,300.);//iMouse.x;
    float c = (n*n) / (uv.x*uv.y);

    vec3 col = vec3(cos(c),0.,0.);

    float w = iMouse.y / iResolution.y/2.;
    col = smoothstep(0., 0.01, col);
    //col *= 10.;
    // Output to screen
    fragColor = vec4(col,1.0);
}
