#define H(P) int( 1e3* sin( dot(P,R-17.) - ceil(iTime) )) % 2  < 1 // boolean rand 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, I,F,
         U = 4.* u / R.y; // + .1*iTime;
   U.x += .5*U.y;                                       // tilted 2D tiling
   U.y /= .87;
   I = ceil(U);                                         // tile Id
   U = 3.*fract(U);                                     // subtiles
   F = fract(U);
   int c = 2*int(U) + 6*int(U.y) + int( F.x > F.y );    // subtile Id ( 18 triangles )
   O = vec4(
       U.x < U.y ?  c<8 ? .5 :  c>11 && c<15 ? 1. : .2  // up semi-tile
                 :  c<3 ? H(I   ) ? 1. : c<2 ? .5 : .2  // down semi-tile
                  : c<4 ? 1.
                  : c<6 ? H(I+.3) ? .5 : c<5 ? 1. : .2  // H(): random relative order
                  : c<10 ? .2                           //      of the 3 cubes
                  : c==11? .5
                  :       H(I+.7) ? .2 : c<17 ? .5 : 1.
           );

// O = vec4(c)/18.;
// if (iMouse.z>0.) O += .3*vec4( mod(I.x+I.y,2.) ,0, U.x-U.y < 0. ,0);  // debug
}
