void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord/iResolution.xy;
vec2 st = uv-.5;
float d = length(st);


vec3 col = vec3(0.01);

vec2 offs = st*.008*d;
col.r += texture(iChannel0, uv-offs).r;
col.g += texture(iChannel0, uv).r;
col.b += texture(iChannel0, uv+offs).r;

// Add a time-based rotation to the moire pattern
float rotation = iTime * 0.5;
vec2 rotateCoord = mat2(cos(rotation), -sin(rotation), sin(rotation), cos(rotation)) * uv;
float pattern = sin(rotateCoord.x * 100.0) * cos(rotateCoord.y * 100.0);

// Use the pattern value to modify the color
col += vec3(pattern);



fragColor = vec4(col,5.0);
}
