#ifdef GL_ES
precision mediump float;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy - iResolution.xy * .5) / iResolution.yy + 0.5;

    float movement = 0.125 * sin(uv.y * 13.0 + iTime * 0.2) + 0.15 * sin(uv.y * 17.0 + iTime * 1.);

    uv.x += movement * pow(uv.y, 0.5);

    uv.x *= 15.0;

    uv.x = fract(uv.x); 

    vec4 color1 = vec4(0.0, 0.0157, 1.0, 1.0);
    vec4 color2 = vec4(0.0, 0.0196, 0.2039, 1.0);

    float t = smoothstep(0.2, 0.3, uv.x) - smoothstep(0.7, 0.8, uv.x);

    vec4 color = mix(color1, color2, t);

    fragColor = vec4(color);
}
