void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 result;

    vec3 a = vec3(0.0,0.0,0.0);
    vec3 b = vec3(800.0,0.0,0.0);
    vec3 c = vec3(400.0,450.0,0.0);

    vec3 u = cross(vec3(c.x-a.x, b.x-a.x, a.x-fragCoord.x), vec3(c.y-a.y, b.y-a.y, a.y-fragCoord.y));

    result = vec3(1.f-(u.x+u.y)/u.z, u.y/u.z, u.x/u.z);
    if (result.x<0.0 || result.y<0.0 || result.z<0.0)
    {
        result = vec3(0, 0, 0);
    }
    result = vec3(dFdx(result));
    // Output to screen
    fragColor = vec4(result, 1.0);
}
