void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // 利用uv实现渐变效果,从右到左
    // float strength = uv.x;

    // 利用uv实现渐变效果,从左到右
    // float strength = 1.0 - uv.x;

    // 利用uv实现渐变效果,从上到下
    // float strength = uv.y;

    // 利用uv实现渐变效果,从下到上
    float strength = 1.0 - uv.y;

    vec3 redColor = vec3(1.0, 0.0, 0.0);
    vec3 blueColor = vec3(0.0, 0.0, 1.0);
    // vec3 uvColor = vec3(uv,1.0);

    vec3 mixColor =  mix(redColor, blueColor, strength);

    // Output to screen
    fragColor = vec4(mixColor, 1.0);
}
