void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 aspect_w = vec2( iResolution.x/iResolution.y, 1.0 );
    vec2 scale = vec2( 2.0 );

    vec2 uv = (fragCoord/iResolution.xy - vec2(0.5)) * aspect_w * scale + vec2(0.5);
    vec2 tex_res = iChannelResolution[0].xy;

    vec2 half_pixel = vec2(0.5) / tex_res;

    // uv clamp
    uv = clamp(uv, half_pixel, vec2(1) - half_pixel);

    // texture access
    float lod = 0.0;
    fragColor = texture(iChannel0, uv, lod);

}
