#define PI 3.1415926538

float blob(vec2 pt, vec2 center, float radius){
    pt = pt - center;
    // a . b = |a||b|cos(Î¸)
    vec2 b  = pt.x < 0.0 ? vec2(0.0, 1.0) : vec2(0.0, -1.0);
    float dot = dot(pt, b) ;
    float a = acos(dot/(length(b)*length(pt)));
    float dist = length(pt)  ;
    return 1.0 - smoothstep(0.002, -0.002, (radius - sin(a * 10.0 + iTime * 3.0) * radius * 0.1) - dist);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y/iResolution.x;

    // Time varying pixel color
    float repeat = step(fract(uv.x * 20.0), 0.5);


    float c = blob(uv * vec2(1.0, aspect), vec2(0.5) * vec2(1.0, aspect), 0.15 );

    vec3 color = vec3(mod((repeat + c ), 2.0));

    // Output to screen
    fragColor = vec4(color,1.0);
}
