#define R iResolution.xy
#define S smoothstep
#define P 3.14159265
#define offset .1257
#define lines 250.
mat2 rot(float a) {
    return mat2(cos(a), sin(-a), sin(a), cos(a));
}
float C (vec2 uv, float angle) {
    vec2 st = uv * rot(angle);
    float m = smoothstep(-.75, .3, atan(st.x, st.y)/P); 
    return S(.5,.75, cos(length(uv) * lines))* S(0.001, 0., abs(length(uv)- offset)-.12) * m;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*R.xy)/R.y,
        st = uv - vec2(-offset, offset);
    if (st.x > 0. && st.y < 0. && st.y > -offset * 2.) st.y = 0.;
    float c = C(st, P*1.5);

    st = uv - vec2(offset, -offset);
    if (st.x < 0. && st.y > 0. && st.y < offset * 2.) st.y = 1.;
    c += C(st, P*.5);

    fragColor = vec4(c);
}
